using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

using Android.App;
using Android.Content;
using Android.Graphics;
using Android.Locations;
using Android.OS;
using Android.Runtime;
using Android.Views;
using Android.Views.InputMethods;
using Android.Widget;

namespace InService.App.UI
{
    static class ExtensionMethods
    {
        public static void HideSoftKeyboard(this Activity context)
        {
            if (context.CurrentFocus != null)
            {
                InputMethodManager imm = (InputMethodManager)context.GetSystemService(Context.InputMethodService);
                imm.HideSoftInputFromWindow(context.CurrentFocus.WindowToken, 0);
            }
        }
        public static Bitmap GetCroppedBitmap(this Bitmap bmp, int radius)
        {
            Bitmap sbmp;

            if (bmp.Width != radius || bmp.Height != radius)
            {
                float smallest = Math.Min(bmp.Width, bmp.Height);
                float factor = smallest / radius;
                sbmp = Bitmap.CreateScaledBitmap(bmp,
                        (int)(bmp.Width / factor),
                        (int)(bmp.Height / factor), false);
            }
            else sbmp = bmp;

            Bitmap output = Bitmap.CreateBitmap(radius, radius, Bitmap.Config.Argb8888);
            Canvas canvas = new Canvas(output);

            uint color = 0xffa19774;
            Paint paint = new Paint();
            Rect rect = new Rect(0, 0, radius, radius);

            paint.AntiAlias = true;
            paint.FilterBitmap = true;
            paint.Dither = true;
            canvas.DrawARGB(0, 0, 0, 0);
            paint.Color = new Color((int)color);
            canvas.DrawCircle(radius / 2, radius / 2, radius / 2, paint);
            paint.SetXfermode(new PorterDuffXfermode(PorterDuff.Mode.SrcIn));
            canvas.DrawBitmap(sbmp, rect, rect, paint);

            return output;
        }

        public static Bitmap ToRoundedCornerBitmap(this Bitmap bitmap, int pixels)
        {
            Bitmap output = Bitmap.CreateBitmap(bitmap.Width, bitmap.Height, Bitmap.Config.Argb8888);
            Canvas canvas = new Canvas(output);

            var color = 0xff424242;
            var paint = new Paint();
            var rect = new Rect(0, 0, bitmap.Width, bitmap.Height);
            var rectF = new RectF(rect);
            var roundPx = pixels;

            paint.AntiAlias = true;
            canvas.DrawARGB(0, 0, 0, 0);
            paint.Color = new Color((int)color); ;
            canvas.DrawRoundRect(rectF, roundPx, roundPx, paint);

            paint.SetXfermode(new PorterDuffXfermode(PorterDuff.Mode.SrcIn));
            canvas.DrawBitmap(bitmap, rect, rect, paint);

            return output;
        }

        public static bool IsGPSEnabled(this Context mContext)
        {
            LocationManager locationManager = (LocationManager)mContext.GetSystemService(Context.LocationService);
            return locationManager.IsProviderEnabled(LocationManager.GpsProvider);
        }

        public static Location GetCurrentGPSLocation(this Context context)
        {
            LocationManager locationManager = (LocationManager)context.GetSystemService(Context.LocationService);
            var bestProvider = locationManager.GetBestProvider(new Criteria() { Accuracy = Accuracy.Medium, AltitudeRequired = false, BearingAccuracy = Accuracy.NoRequirement }, true);
            if (!string.IsNullOrWhiteSpace(bestProvider)) return locationManager.GetLastKnownLocation(bestProvider);
            return null;
        }

        public static Bitmap BaseStringToBitmap(this string bse64)
        {
            var placeholder = "";
            byte[] imageBytes = !string.IsNullOrWhiteSpace(bse64) ? Convert.FromBase64String(bse64) : Convert.FromBase64String(placeholder);
            return BitmapFactory.DecodeByteArray(imageBytes, 0, imageBytes.Length);
        }

        public static DateTime GetDateFromJavaTime(this long JavaTime)
        {
            DateTime UTCBaseTime = new DateTime(1970, 1, 1, 0, 0, 0, DateTimeKind.Utc);
            return UTCBaseTime.Add(new TimeSpan(JavaTime * TimeSpan.TicksPerMillisecond)).ToLocalTime();
        }

        public static long GetJavaTimeFromDate(this DateTime Date)
        {
            DateTime UTCBaseTime = new DateTime(1970, 1, 1, 0, 0, 0, DateTimeKind.Utc).ToLocalTime();
            return (long)Date.Subtract(UTCBaseTime).TotalMilliseconds;
        }

        public static Guid ID { get; } = Guid.Parse("00000000-0000-0000-0000-000000000000");
    }
}